import { ARTICLE_DATA_LOADED } from "./feedActions";

const initialState = {
  // Blank for now
};

export const feedReducer = (state = initialState, action) => {
  ///数据变换
  const tempTagList = [];
  state.articleLibrary && state.articleLibrary.forEach(article => {
    if (article.tagList.length !== 0)
      article.tagList.forEach(item => {
        console.log(item);
        tempTagList.push(item);
        // console.log(tempTagList)
      });
  });

  // // 数据统计，显示前五
  // const myObject = tempTagList.reduce(reducer, {});
  // setPopularTags(Object.keys(myObject).slice(0, 4));

  switch (action.type) {
    case ARTICLE_DATA_LOADED:
      return { ...state, articleLibrary: action.articleInitData };
    default:
      return state;
  }
};
