  const reducer = (allElements, element) => {
    if (element in allElements) allElements[element]++;
    else allElements[element] = 1;
    return allElements;
  };

  console.log(props.articleLibrary);

  return (
    <div class='col-md-3'>
      <div class='sidebar'>
        <p>Popular Tags</p>

        <div class='tag-list'>
          {popularTags.map((popular)=>{


              return(
                <a href='#top' class='tag-pill tag-default'>
                  {popular}
                </a>
              )
          })}

        </div>
      </div>
    </div>
  );
};

