import React from "react";
import { connect } from "react-redux";

const InternalPopularTages = props => {
  const tempTagList = [];

const ListCounter = (array) =>{
  // 取出每一个元素，建立一对一的关系
  // 每次取出的一个元素，在一对一关系上面累加
  // 最后形成 {元素：n， 元素：n的关系 }
  array.reduce(function (accumulator, currentValue) {
    return accumulator + currentValue
  }, 1)  
  }


  return (
    <div class='col-md-3'>
      <div class='sidebar'>
        <p>Popular Tags</p>

        <div class='tag-list'>
          {props.articleLibrary &&
            props.articleLibrary.forEach(article => {
              if (article.tagList.length !== 0) {
                article.tagList.forEach(item => {
                  tempTagList.push(item);
                });
              }
              console.log(tempTagList.sort());
              // 首先排序
              // 统计每个元素出现的个数
              // 取出前几名
            })}
          <a href='#top' class='tag-pill tag-default'>
            programming
          </a>
          <a href='#top' class='tag-pill tag-default'>
            programming
          </a>
        </div>
      </div>
    </div>
  );
};

const mapStateToProps = state => {
  return { ...state };
};

const mapDismatchToProps = dispatch => {
  return {};
};

export const PopularTages = connect(
  mapStateToProps,
  mapDismatchToProps
)(InternalPopularTages);
