import { ARTICLE_DATA_LOADED } from "./feedActions";
import { connect } from "react-redux";

const initialState = {
  // Blank for now
};

export const InternalFeedReducer = (state = initialState, action) => {
  ///数据变换
  const tempTagList = [];
  console.log(state.articleLibrary);

  props.articleLibrary && state.articleLibrary.forEach(article => {
    if (article.tagList.length !== 0)
      article.tagList.forEach(item => {
        console.log(item);
        tempTagList.push(item);
        // console.log(tempTagList)
      });
  });

  // // 数据统计，显示前五
  // const myObject = tempTagList.reduce(reducer, {});
  // setPopularTags(Object.keys(myObject).slice(0, 4));

  switch (action.type) {
    case ARTICLE_DATA_LOADED:
      return { ...state, articleLibrary: action.articleInitData };
    default:
      return state;
  }
};


const mapStateToProps = state => {
  return { ...state };
};

const mapDismatchToProps = dispatch => {
  return {};
};

export const feedReducer = connect(
  mapStateToProps,
  mapDismatchToProps
)(InternalFeedReducer);
